import { SchemaOrgResolver } from '@vueuse/schema-org';
import SchemaOrg from '@vueuse/schema-org/vite';

function schemaOrgIles(pluginOptions) {
  return {
    name: "@vueuse-schema-org/schema-org-iles",
    components: {
      resolvers: [
        SchemaOrgResolver()
      ]
    },
    config(config) {
      config.vite = config.vite || {};
      config.vite.plugins = config.vite.plugins || [];
      config.vite.plugins.push(SchemaOrg({
        full: false,
        dts: true,
        mock: false,
        ...pluginOptions
      }));
    }
  };
}

export { schemaOrgIles };
