import { MetaFlat, Head, MergeHead } from '@zhead/schema';
import { ReactiveHead, MetaFlatRef, RawReactiveHead } from '@zhead/schema-vue';
export * from '@zhead/schema-vue';
import { Ref } from 'vue';
import { MaybeComputedRef } from '@vueuse/shared';

declare function packMeta<T extends ReactiveHead['meta']>(input: T): Ref<MetaFlat>;
declare function unpackMeta<T extends MaybeComputedRef<MetaFlatRef>>(input: T): Ref<Required<Head>['meta']>;

declare function defineHead<T extends MergeHead>(input: ReactiveHead<T>): ReactiveHead<T>;
declare function defineHeadRaw<T extends MergeHead>(input: RawReactiveHead<T>): RawReactiveHead<T>;

export { defineHead, defineHeadRaw, packMeta, unpackMeta };
